DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `username` varchar(20) DEFAULT '' COMMENT '用户名',
  `nickname` varchar(20) NOT NULL COMMENT '昵称',
  `access_token` varchar(255) DEFAULT NULL COMMENT '身份标识',
  `password_hash` varchar(255) NOT NULL COMMENT '密码hash 值',
  `password_reset_token` varchar(255) DEFAULT NULL COMMENT '重置密码的标识',
  `mobile` varchar(20) NOT NULL COMMENT '手机号',
  `email` varchar(50) DEFAULT '' COMMENT '邮箱',
  `avatar_url` varchar(200) DEFAULT '' COMMENT '头像URL',
  `last_address_id` int(10) DEFAULT '0' COMMENT '最近一次使用的地址ID',
  `last_ip` varchar(20) DEFAULT NULL COMMENT '最近一次登录的IP',
  `last_device_type` varchar(20) DEFAULT NULL COMMENT '最近一次登录的设备类型',
  `last_device_id` varchar(20) DEFAULT NULL COMMENT '最近一次登录的设备ID',
  `created_at` int(20) DEFAULT NULL COMMENT '创建时间',
  `updated_at` int(20) DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

